s' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -72-8 hour),'%Y-%m-%d') else case when pf.timezone='Europe/London' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -72-7 hour),'%Y-%m-%d') else DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -72 hour),'%Y-%m-%d') end end end then pdt.cost else 0 end cost2, case when pdt.bydate= case when pf.timezone='America/Los_Angeles' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -48-16 hour),'%Y-%m-%d') else case when pf.timezone='Europe/Paris' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -48-8 hour),'%Y-%m-%d') else case when pf.timezone='Europe/London' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -48-7 hour),'%Y-%m-%d') else DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -48 hour),'%Y-%m-%d') end end end then attributedConversions7d else 0 end order1, case when pdt.bydate= case when pf.timezone='America/Los_Angeles' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -72-16 hour),'%Y-%m-%d') else case when pf.timezone='Europe/Paris' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -72-8 hour),'%Y-%m-%d') else case when pf.timezone='Europe/London' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -72-7 hour),'%Y-%m-%d') else DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -72 hour),'%Y-%m-%d') end end end then attributedConversions7d else 0 end order2, case when pdt.bydate= case when pf.timezone='America/Los_Angeles' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -48-16 hour),'%Y-%m-%d') else case when pf.timezone='Europe/Paris' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -48-8 hour),'%Y-%m-%d') else case when pf.timezone='Europe/London' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -48-7 hour),'%Y-%m-%d') else DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -48 hour),'%Y-%m-%d') end end end then attributedSales7d else 0 end sales1, case when pdt.bydate= case when pf.timezone='America/Los_Angeles' then DATE_FORMAT(date_add(CURRENT_TIM