replace into t_advert_warning_keywords_data select v.id,v.shopid,'co',v.groupName,v.market,v.campaignName,v.adGroupName,v.keywordText, sum(v.click0) clicks,sum(v.impressions0) impressions,sum(v.cost0) cost,sum(v.order0) orders,sum(v.sales0) sales, null clicksp,null impressionsp,null costp,null ordersp,null salesp , sum(v.click2) clickscp,sum(v.impressions3) impressionscp,sum(v.cost2) costcp,sum(v.order3) orderscp,sum(v.sales2) salescp from ( select a.shopid,pdt.keywordId id, g.name groupName, m.name market, cm.name campaignName, ad.name adGroupName, k.keywordText keywordText, case when pdt.bydate= case when pf.timezone='America/Los_Angeles' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -24-16 hour),'%Y-%m-%d') else case when pf.timezone='Europe/Paris' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -24-8 hour),'%Y-%m-%d') else case when pf.timezone='Europe/London' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -24-7 hour),'%Y-%m-%d') else DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -24 hour),'%Y-%m-%d') end end end then pdt.clicks else 0 end click0, case when pdt.bydate= case when pf.timezone='America/Los_Angeles' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -24-16 hour),'%Y-%m-%d') else case when pf.timezone='Europe/Paris' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -24-8 hour),'%Y-%m-%d') else case when pf.timezone='Europe/London' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -24-7 hour),'%Y-%m-%d') else DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -24 hour),'%Y-%m-%d') end end end then pdt.impressions else 0 end impressions0, case when pdt.bydate= case when pf.timezone='America/Los_Angeles' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -24-16 hour),'%Y-%m-%d') else case when pf.timezone='Europe/Paris' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -24-8 hour),'%Y-%m-%d') else case when pf.timezone='Europe/London' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -24-7 hour),'%Y-%m-%d') else DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -24 hour),'%Y-%m-%d') end end end then pdt.cost else 0 end cost0, case when pdt.bydate= case when pf.timezone='America/Los_Angeles' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -24-16 hour),'%Y-%m-%d') else case when pf.timezone='Europe/Paris' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -24-8 hour),'%Y-%m-%d') else case when pf.timezone='Europe/London' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -24-7 hour),'%Y-%m-%d') else DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -24 hour),'%Y-%m-%d') end end end then attributedConversions7d else 0 end order0, case when pdt.bydate= case when pf.timezone='America/Los_Angeles' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -24-16 hour),'%Y-%m-%d') else case when pf.timezone='Europe/Paris' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -24-8 hour),'%Y-%m-%d') else case when pf.timezone='Europe/London' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -24-7 hour),'%Y-%m-%d') else DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -24 hour),'%Y-%m-%d') end end end then attributedSales7d else 0 end sales0,
case when pdt.bydate= case when pf.timezone='America/Los_Angeles' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -168-16 hour),'%Y-%m-%d') else case when pf.timezone='Europe/Paris' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -168-8 hour),'%Y-%m-%d') else case when pf.timezone='Europe/London' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -168-7 hour),'%Y-%m-%d') else DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -168 hour),'%Y-%m-%d') end end end then pdt.clicks else 0 end click2, case when pdt.bydate= case when pf.timezone='America/Los_Angeles' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -168-16 hour),'%Y-%m-%d') else case when pf.timezone='Europe/Paris' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -168-8 hour),'%Y-%m-%d') else case when pf.timezone='Europe/London' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -168-7 hour),'%Y-%m-%d') else DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -168 hour),'%Y-%m-%d') end end end then pdt.impressions else 0 end impressions3, case when pdt.bydate= case when pf.timezone='America/Los_Angeles' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -168-16 hour),'%Y-%m-%d') else case when pf.timezone='Europe/Paris' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -168-8 hour),'%Y-%m-%d') else case when pf.timezone='Europe/London' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -168-7 hour),'%Y-%m-%d') else DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -168 hour),'%Y-%m-%d') end end end then pdt.cost else 0 end cost2, case when pdt.bydate= case when pf.timezone='America/Los_Angeles' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -168-16 hour),'%Y-%m-%d') else case when pf.timezone='Europe/Paris' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -168-8 hour),'%Y-%m-%d') else case when pf.timezone='Europe/London' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -168-7 hour),'%Y-%m-%d') else DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -168 hour),'%Y-%m-%d') end end end then attributedConversions7d else 0 end order3, case when pdt.bydate= case when pf.timezone='America/Los_Angeles' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -168-16 hour),'%Y-%m-%d') else case when pf.timezone='Europe/Paris' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -168-8 hour),'%Y-%m-%d') else case when pf.timezone='Europe/London' then DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -168-7 hour),'%Y-%m-%d') else DATE_FORMAT(date_add(CURRENT_TIMESTAMP , interval -168 hour),'%Y-%m-%d') end end end then attributedSales7d else 0 end sales2 from t_amz_adv_rpt2_sp_keywords pdt left join t_amz_adv_rpt2_sp_keywords_attributed d on d.keywordId=pdt.keywordId and d.bydate=pdt.bydate left join t_amz_adv_rpt2_sp_keywords_attributed_same sa on sa.bydate=pdt.bydate and sa.keywordId=pdt.keywordId left join t_amz_adv_adgroups ad on ad.adGroupId=pdt.adGroupId and ad.campaignId=pdt.campaignId and pdt.profileid=ad.profileid left join t_amz_adv_campaigns cm on cm.campaignId=pdt.campaignId and cm.profileid=pdt.profileid left join t_amz_adv_keywords k on pdt.keywordId = k.keywordId and pdt.adGroupId= k.adGroupId and pdt.campaignId = k.campaignId and pdt.profileid = k.profileid
left join t_amz_adv_profile pf on pf.id=pdt.profileid left join t_marketplace m on m.marketplaceid=pf.marketplaceid left join t_amz_adv_auth a on a.id=pf.advauthId and a.disable = 0 left join t_amazon_group g on g.id=a.groupid where (pdt.bydate=date_add(curdate() , interval -1 day) or pdt.bydate=date_add(curdate() , interval -2 day) or pdt.bydate=date_add(curdate() , INTERVAL -7 day) or pdt.bydate=date_add(curdate() , INTERVAL -8 day) ) and pdt.profileid=#{profileid,jdbcType=CHAR} group by pdt.keywordId,pdt.bydate ) v group by v.id having 1=1;

mapper.select:
	 	 select *,orders0/click0 cr0,orders1/click1 cr1,orders2/click2 cr2, 
 			cost0/sales0 acos0,cost1/sales1 acos1,cost2/sales2 acos2,
 			(impressions1-impressions0)/impressions1 impressionsyesterdayrate,
 			((impressions2-impressions1)/impressions2+(impressions1-impressions0)/impressions1)/2 
 			 impressionssequentrate,
 			 (impressions2-impressions0)/impressions2 impressionscorate,

 			 (click1-click0)/click1 clickyesterdayrate,
 			 ((click2-click1)/click2+(click1-click0)/click1)/2  clicksequentrate,
 			 (click2-click0)/click2 clickcorate,


 			  (orders1/click1-orders0/click0)/(orders1/click1) cryesterdayrate,
 			  ((orders2/click2-orders1/click1)/(orders2/click2)+(orders1/click1-orders0/click0)/(orders1/click1))/2 crsequentrate,
 			 (orders2/click2-orders0/click0)/(orders2/click2) crcorate,

 			  (cost0/sales0-cost1/sales1)/(cost0/sales0) acosyesterdayrate,
 			 ( (cost0/sales0-cost1/sales1)/(cost0/sales0) + (cost1/sales1-cost2/sales2)/(cost1/sales1))/2  acossequentrate,
 			 (cost0/sales0-cost2/sales2)/(cost0/sales0) acoscorate

	 	  from t_advert_warning_keywords_data where shopid=#{shopid,jdbcType=CHAR}

			and impressions1>0
			and (impressions1-impressions0)>#{absoluteNumImpressions,jdbcType=INTEGER}
			and (impressions1-impressions0)/impressions1>#{numImpressions,jdbcType=DECIMAL}


			and impressions1>0 and impressions2>0
			and (impressions1-impressions0)>#{absoluteNumImpressions,jdbcType=INTEGER}
			and (impressions2-impressions1)>#{absoluteNumImpressions,jdbcType=INTEGER}
			and (impressions1-impressions0)/impressions1>#{numImpressions,jdbcType=DECIMAL} 
			and (impressions2-impressions1)/impressions2>#{numImpressions,jdbcType=DECIMAL} 


			and impressions2>0 and impressions1 is null
			and (impressions2-impressions0)>#{absoluteNumImpressions,jdbcType=INTEGER}
			and (impressions2-impressions0)/impressions2>#{numImpressions,jdbcType=DECIMAL}


			and click1>0 
			and (click1-click0)>#{absoluteNumClicks,jdbcType=INTEGER}
			and (click1-click0)/click1>#{numClicks,jdbcType=DECIMAL}


	      	and click1>0 and click2>0  
	      	and (click1-click0)>#{absoluteNumClicks,jdbcType=INTEGER}
	      	and (click2-click1)>#{absoluteNumClicks,jdbcType=INTEGER}
		  	and (click1-click0)/click1>#{numClicks,jdbcType=DECIMAL} 
		  	and (click2-click1)/click2>#{numClicks,jdbcType=DECIMAL}


	      	and click2>0 and click1 is null
	      	and (click2-click0)>#{absoluteNumClicks,jdbcType=INTEGER}
	      	and (click2-click0)/click2)>#{numClicks,jdbcType=DECIMAL} 


	     	and click0>0 and click1>0 and orders1>0
	     	and (orders1/click1-orders0/click0)>#{absoluteNumCSRT,jdbcType=DECIMAL} 
	      	and (orders1/click1-orders0/click0)/(orders1/click1)>#{numCSRT,jdbcType=DECIMAL}


	      	and click0>0 and click1>0 and click2>0 and orders1>0 and orders2>0
	      	and (orders1/click1-orders0/click0)>#{absoluteNumCSRT,jdbcType=DECIMAL} 
	      	and (orders2/click2-orders1/click1)>#{absoluteNumCSRT,jdbcType=DECIMAL} 
		  	and (orders1/click1-orders0/click0)/(orders1/click1)>#{numCSRT,jdbcType=DECIMAL} 
		  	and (orders2/click2-orders1/click1)/(orders2/click2)>#{numCSRT,jdbcType=DECIMAL} 


	      	and click0>0 and click2>0 and orders2>0 and click1 is null and orders1 is null
	      	and (orders2/click2-orders0/click0)>#{absoluteNumCSRT,jdbcType=DECIMAL}  
	      	and (orders2/click2-orders0/click0)/(orders2/click2)>#{numCSRT,jdbcType=DECIMAL}


	      	and sales0>0 and sales1>0 and cost0>0
	      	and (cost0/sales0-cost1/sales1)>#{absoluteNumACOS,jdbcType=DECIMAL} 
	      	and (cost0/sales0-cost1/sales1)/(cost0/sales0)>#{numACOS,jdbcType=DECIMAL}


	      	and sales0>0 and sales1>0 and sales2>0 and cost1>0 and cost0>0
	      	and (cost0/sales0-cost1/sales1)>#{absoluteNumACOS,jdbcType=DECIMAL}
	      	and (cost1/sales1-cost2/sales2)>#{absoluteNumACOS,jdbcType=DECIMAL}
		  	and (cost0/sales0-cost1/sales1)/(cost0/sales0)>#{numACOS,jdbcType=DECIMAL} 
		  	and (cost1/sales1-cost2/sales2)/(cost1/sales1)>#{numACOS,jdbcType=DECIMAL} 


	      	and sales0>0 and sales2>0 and cost0>0  and sales1 is null
	      	and (cost0/sales0-cost2/sales2)>#{absoluteNumACOS,jdbcType=DECIMAL} 
	      	and (cost0/sales0-cost2/sales2)/(cost0/sales0)>#{numACOS,jdbcType=DECIMAL}

