select uuid() id, CAST(t.adGroupId AS CHAR) adGroupId, CAST(t.campaignId AS CHAR) campaignId, CAST(t.targetId AS CHAR) targetId, CAST(t.profileid AS CHAR) profileid, t.expressionType, t.expression,t.bid, tr.cycle,tr.fcondition,tr.subtrahend,tr.quota,tr.amount, t.state status,mk.name market,tk.remark,tr.iswarn iswarn,p.marketplaceId marketplaceid, a.groupid groupid,tg.name groupname,v.*,tc.name campaignName,tc.state campaignStatus, tgp.name adGroupName,tgp.state adGroupStatus ,t.opttime from ( select t.campaignId campaignId1,t.profileid profileid1,t.adGroupId adGroupId1,t.targetId targetId1,q.query query, sum(impressions) impressions,sum(clicks) clicks,sum(cost) cost,ta.expression targetingText, ta.expressionType targetingType, ifnull((sum(cost) / sum(clicks)),0) avgcost, ifnull((sum(clicks) / sum(impressions)),0) CTR, case when ifnull(sum(cost),0)>0 and ifnull(sum(attributedSales7d),0)=0 then 10000000000 else ifnull((sum(cost) / sum(attributedSales7d)),0) end ACOS, ifnull((sum(attributedSales7d) / sum(cost)),0) ROAS, ifnull(sum(attributedSales7d),0) sumSales, ifnull(sum(attributedUnitsOrdered7d),0) sumUnits, ifnull((sum(attributedConversions7d) / sum(clicks)),0) CSRT, ifnull(sum(attributedConversions7d) - sum(attributedConversions7dSameSKU),0) attributedConversions, ifnull(sum(attributedConversions7dSameSKU),0) attributedConversionsSameSKU, ifnull(sum(attributedConversions7d),0) attributedUnitsOrdered, ifnull(sum(attributedSales7dSameSKU),0) attributedSalesSameSKU, ifnull(sum(attributedSales7d) - sum(attributedSales7dSameSKU),0) attributedSales from t_amz_adv_rpt2_sp_product_targets_query t left join t_amz_adv_rpt2_sp_product_targets_query_attributed d on d.targetId=t.targetId and d.bydate=t.bydate and d.queryid=t.queryid left join t_amz_adv_rpt2_sp_product_targets_query_attributed_same sa on sa.targetId=t.targetId and sa.bydate=t.bydate and sa.queryid=t.queryid left join t_amz_adv_adgroups ad on ad.adGroupId=t.adGroupId and ad.campaignId=t.campaignId and t.profileid=ad.profileid left join t_amz_adv_campaigns cm on cm.campaignId=t.campaignId and cm.profileid=t.profileid left join t_amz_adv_product_targe ta on ta.targetId=t.targetId and ta.adGroupId=t.adGroupId and ta.campaignId=t.campaignId and ta.profileid=t.profileid left join t_amz_adv_rpt_query q on q.id=t.queryid where t.bydate >= #{fromDate,jdbcType=DATE} and t.bydate <= #{endDate,jdbcType=DATE}
and t.profileid = #{profileid,jdbcType=BIGINT}
and t.campaignId = #{campaignid,jdbcType=BIGINT}
and t.adGroupId = #{adGroupid,jdbcType=BIGINT}
and t.query like #{name,jdbcType=CHAR}
