mapper.select:
select a.currency , sum(case when ifnull(a.processing_status,'Open')='Open' then a.original_total else 0 end) totalAmount , sum(case when ifnull(a.processing_status,'Open')='Closed' and ifnull(a.fund_transfer_status,'Unknown')='Failed' then a.original_total else 0 end) totalFailedAmount from t_amz_fin_account a left join t_amazon_auth t on t.id=a.amazonAuthid left join t_amazon_group g on g.id=t.groupid and t.shop_id=g.shopid where t.`disable`=0 and g.shopid=#{shopid,jdbcType=CHAR}
and t.groupid = #{groupid,jdbcType=CHAR}
and a.currency=#{currency,jdbcType=CHAR}
and ( ifnull(a.processing_status,'Open')='Open' or (fund_transfer_date >= #{fromDate,jdbcType=DATE} and fund_transfer_date <= #{endDate,jdbcType=DATE}) )
group by a.currency

select  max(refreshtime) refreshtime
from t_amz_fin_account a
where  amazonAuthid=#{amazonAuthid,jdbcType=CHAR}

select g.name groupname,g.id groupid,a.currency, a.amazonauthid amazonauthid, a.financial_event_group_start startDate,original_total totalAmount, a.groupid accgroupid,'finacc' fatype,a.processing_status pstatus,a.fund_transfer_status tstatus from t_amz_fin_account a left join t_amazon_auth t on t.id=a.amazonAuthid left join t_amazon_group g on g.id=t.groupid and t.shop_id=g.shopid where ifnull(a.processing_status,'Open')='Open' and t.`disable`=0 and g.shopid=#{param.shopid,jdbcType=CHAR}
and t.groupid = #{param.groupid,jdbcType=CHAR}
and a.currency=#{param.currency,jdbcType=CHAR}
