mapper.select:
select v.* from ( select t.sku,max(concat('FBA-',ifnull(w.name,'欧洲'))) warehouse

	,


			sum(case when t.byday =#{str.byday,jdbcType=CHAR} then ifnull(t.afn_warehouse_quantity,0) else 0 end ) #{str.field,jdbcType=CHAR}



from t_inventory_report_his t
left join t_amazon_auth a on a.id = t.amazonAuthId
left join t_marketplace w on w.marketplaceid=t.marketplaceid  
where a.shop_id = #{param.shopid,jdbcType=CHAR} 
	and t.byday >=#{param.beginDate,jdbcType=CHAR}
	and t.byday <=#{param.endDate,jdbcType=CHAR}

	and a.groupid=#{param.groupid,jdbcType=CHAR}


	and t.marketplaceid=#{param.warehouse,jdbcType=CHAR}


	and t.sku like #{param.sku,jdbcType=CHAR}

group by t.sku,t.marketplaceid) v

select count(t.sku) vsku

	,


			sum(case when t.byday =#{str.byday,jdbcType=CHAR} then ifnull(t.afn_warehouse_quantity,0) else 0 end ) #{str.byday,jdbcType=CHAR}



from t_inventory_report_his t
left join t_amazon_auth a on a.id = t.amazonAuthId
where a.shop_id = #{shopid,jdbcType=CHAR} 
	and t.byday >=#{beginDate,jdbcType=CHAR}
	and t.byday <=#{endDate,jdbcType=CHAR}

	and a.groupid=#{groupid,jdbcType=CHAR}


	and t.marketplaceid=#{warehouse,jdbcType=CHAR}


	and t.sku like #{sku,jdbcType=CHAR}
