mapper.select:
select 

from t_amz_product_pageviews p
left join  t_amazon_auth a on p.amazonAuthid =a.id
where   
    a.shop_id= #{shopid,jdbcType=CHAR}

      and  amazonAuthid = #{amazonauthid,jdbcType=CHAR}


      and  byday >= #{startDate,jdbcType=DATE}


      and  byday < #{endDate,jdbcType=DATE}

  and marketplaceid = #{marketplaceid,jdbcType=CHAR}
  and SKU = #{sku,jdbcType=CHAR}

mapper.delete:
delete from t_amz_product_pageviews_download
where byday = #{byday,jdbcType=CHAR}
and marketplaceid = #{marketplaceid,jdbcType=CHAR}
and amazonAuthid = #{amazonauthid,jdbcType=CHAR}

mapper.select:
SELECT t.byday, t.marketplaceid, t.SKU, t.amazonAuthid, t.parent_asin parentasin, t.child_asin childAsin, t.Sessions, t.Session_Percentage SessionPercentage, t.Page_Views PageViews,t.Page_Views_Percentage PageViewsPercentage,t.Buy_Box_Percentage BuyBoxPercentage,t.Units_Ordered UnitsOrdered, t.Units_Ordered_B2B UnitsOrderedB2B, t.Unit_Session_Percentage UnitSessionPercentage, t.Unit_Session_Percentage_B2B UnitSessionPercentageB2B,t.Ordered_Product_Sales OrderedProductSales, t.Ordered_Product_Sales_B2B OrderedProductSalesB2B, t.Total_Order_Items TotalOrderItems,t.Total_Order_Items_B2B TotalOrderItemsB2B ,IFNULL(p.location,p.url) image, info.name, info.asin asin FROM t_amz_product_pageviews t LEFT JOIN t_product_info info ON info.amazonAuthId=t.amazonAuthid AND t.SKU=info.sku AND t.marketplaceid=info.marketplaceid LEFT JOIN t_picture p ON p.id=info.image LEFT JOIN t_product_in_opt o ON o.pid=info.id LEFT JOIN t_erp_material m ON m.sku=ifnull(o.msku,info.sku) AND m.shopid=#{dto.shopid,jdbcType=CHAR} AND m.isDelete=0 WHERE t.marketplaceid=#{dto.marketplaceid,jdbcType=CHAR} AND t.byday >=#{dto.startDate,jdbcType=CHAR} AND t.byday <=#{dto.endDate,jdbcType=CHAR}
and t.amazonAuthid=#{dto.amazonauthid,jdbcType=CHAR}
and o.owner=#{dto.owner,jdbcType=CHAR}
and t.sku like #{dto.search,jdbcType=CHAR}
