mapper.select:
SELECT t.number, case when ifnull(sum(t.inbound),0)!=0 then sum(case when ifnull(t.inbound,0)!=0 then t.startinbound else 0 end ) else max(t.startinbound) end startinbound, sum(t.inbound) inbound,
case when ifnull(sum(t.inbound),0)!=0 then sum(case when ifnull(t.inbound,0)!=0 then t.endinbound else 0 end ) else max(t.endinbound) end endinbound,
case when ifnull(sum(t.fulfillable),0)!=0 then sum(case when ifnull(t.fulfillable,0)!=0 then t.startfulfillable else 0 end ) else max(t.startfulfillable) end startfulfillable,
sum(t.fulfillable) fulfillable,
case when ifnull(sum(t.fulfillable),0)!=0 then sum(case when ifnull(t.fulfillable,0)!=0 then t.endfulfillable else 0 end ) else max(t.endfulfillable) end endfulfillable,
case when ifnull(sum(t.outbound),0)!=0 then sum(case when ifnull(t.outbound,0)!=0 then t.startoutbound else 0 end ) else max(t.startoutbound) end startoutbound, sum(t.outbound) outbound,
case when ifnull(sum(t.outbound),0)!=0 then sum(case when ifnull(t.outbound,0) then t.endoutbound else 0 end ) else max(t.endoutbound) end endoutbound, max(ty.name) typename, max(m.sku) sku, max(m.name) name, max(IFNULL(p.location,p.url)) image, max(u.name) operator, max(t.opttime) opttime, max(w.name) warehousename, max(t.operate) operate FROM t_erp_inventory_record t LEFT JOIN t_erp_material m ON m.id=t.materialid LEFT JOIN t_erp_formtype ty ON ty.id=t.formtype LEFT JOIN t_picture p ON p.id=m.image LEFT JOIN t_userinfo u ON u.id=t.operator LEFT JOIN t_erp_warehouse w ON w.id=t.warehouseid where t.shopid=#{param.shopid,jdbcType=CHAR}
and t.opttime >= #{param.fromDate,jdbcType=TIMESTAMP} and t.opttime <= #{param.toDate,jdbcType=TIMESTAMP}
and m.sku like #{param.search,jdbcType=CHAR}
and t.number like #{param.search,jdbcType=CHAR}
and t.warehouseid=#{param.warehouseid,jdbcType=CHAR}
and t.operator=#{param.operator,jdbcType=CHAR}
and t.formtype in (
#{types,jdbcType=CHAR}
)
group by t.shopid,t.warehouseid,t.materialid,t.number,t.opttime

  	SELECT t.materialid,SUM(t.fulfillable) fulfillable FROM t_erp_inventory_record t 
	WHERE t.shopid=#{param.shopid,jdbcType=CHAR}  AND t.materialid=#{param.materialid,jdbcType=CHAR}
	AND t.opttime>=#{param.fromDate,jdbcType=CHAR} AND t.opttime<=#{param.toDate,jdbcType=CHAR}

		AND t.fulfillable>0


		AND t.fulfillable<0


		AND t.warehouseid=#{param.warehouseid,jdbcType=CHAR}

	GROUP BY t.materialid

    select sum(case when (iv.`status`='fulfillable')  then iv.quantity else 0 end) fulfillable
	from t_erp_inventory iv
	where iv.quantity >0 
	and iv.materialid=#{materialid,jdbcType=CHAR}
	and iv.shopid=#{shopid,jdbcType=CHAR}

		AND iv.warehouseid=#{warehouseid,jdbcType=CHAR}
