mapper.select:
select m.id, m.name name, m.issfg issfg, p1.location image, ifnull(m.boxnum,0) boxnum, m.sku, mn.mark notice, mc.material material, mc.addfee addfee, case when mh.materialid is null then 0 else 1 end ishide, ifnull(inv.fulfillable,0) fulfillable, ifnull(inv.outbound,0) outbound, ifnull(inv.inbound,0) inbound,
v.amount canAssembly, ifnull(inv.fulfillable,0) quantity,
w.id warehouseid, w.name warehousename, item.groupid, item.amount reallyamount, item.amount, ifnull(inv.fulfillable,0)+ifnull(inv.inbound,0) quantity,
m.issfg, m.price, m.delivery_cycle, ifnull(dd.length,0)*ifnull(dd.width,0)*ifnull(dd.height,0) pkgcm3, ifnull(dd.weight,0) pkgweight, ifnull(bd.length,0)*ifnull(bd.width,0)*ifnull(bd.height,0) boxcm3, ifnull(bd.weight,0) boxweight from t_erp_material m left join t_picture p1 on p1.id=m.image left join t_dimensions dd on dd.id=m.pkgDimensions left join t_dimensions bd on bd.id=m.boxDimensions left join t_erp_material_mark mn on mn.materialid=m.id and mn.ftype='notice' left join t_erp_material_mark mh on mh.materialid=m.id and mh.ftype='phide' left join t_userinfo usi on usi.id=mn.operator
left join (SELECT e.mainmid, min(FLOOR(case when canas>ifnull(inv.quantity,0)/(e.units) then ifnull(inv.quantity,0)/(e.units) ELSE canas END)) amount FROM ( SELECT f.mainmid,e.materialid, max(e.amount/f.amount) units, SUM(f.amount-f.amount_handle) canas from t_erp_assembly_form f left join t_erp_assembly_form_entry e on f.id=e.formid WHERE f.warehouseid=#{warehouseid,jdbcType=CHAR} and f.shopid=#{shopid,jdbcType=CHAR} and f.ftype='ass' and (auditstatus=1 OR auditstatus=2) AND (f.amount>ifnull(f.amount_handle,0)) GROUP BY f.mainmid,e.materialid ) e left join t_erp_inventory inv on inv.materialid=e.materialid and inv.warehouseid=#{warehouseid,jdbcType=CHAR} and inv.status='fulfillable' group by mainmid) v on v.mainmid=m.id left join ( SELECT i.materialid, sum( case when substring_index(status,'_',1)='inbound' then quantity else 0 end ) inbound, sum( case when substring_index(status,'_',1)='outbound' then quantity else 0 end ) outbound, sum( case when substring_index(status,'_',1)='fulfillable' then quantity else 0 end ) fulfillable from t_erp_inventory i where i.shopid=#{shopid,jdbcType=CHAR} and i.warehouseid=#{warehouseid,jdbcType=CHAR} group by i.shopid,warehouseid,i.materialid ) inv on inv.materialid=m.id
left join ( SELECT i.materialid, sum( case when substring_index(status,'_',1)='inbound' then quantity else 0 end ) inbound, sum( case when substring_index(status,'_',1)='outbound' then quantity else 0 end ) outbound, sum( case when substring_index(status,'_',1)='fulfillable' then quantity else 0 end ) fulfillable from t_erp_inventory i left join t_erp_v3_purchase_plan_warehouse w on w.warehouseid=i.warehouseid where i.shopid=#{shopid,jdbcType=CHAR}
and w.planid=#{planid,jdbcType=CHAR}
group by i.shopid,i.materialid ) inv on inv.materialid=m.id left join t_erp_v3_purchase_plan_warehouse_material pw on pw.materialid=m.id and pw.planid=#{planid,jdbcType=CHAR} left join t_erp_v3_purchase_plan_item item on item.materialid=m.id and item.planid=#{planid,jdbcType=CHAR} and item.shopid=#{shopid,jdbcType=CHAR} left join t_erp_warehouse w on w.id=pw.warehouseid
left join t_erp_material_customs mc on mc.materialid=m.id where m.shopid=#{shopid,jdbcType=CHAR} and m.isdelete=false
and m.sku in
#{msku,jdbcType=CHAR}
and item.amount>0
and m.sku ='###'
and m.owner = #{owner,jdbcType=CHAR}
and m.issfg = '1'
and (m.issfg ='0' or m.issfg ='2')
and mn.mark like #{remark,jdbcType=CHAR}
and mh.ftype is null
and mh.ftype='phide'
and m.name like #{name,jdbcType=CHAR}
and m.categoryid=#{categoryid,jdbcType=CHAR}
and ifnull(mc.addfee,0)>0
and ifnull(mc.addfee,0)=0

 select * from (
	select m.id, m.sku sku,m.color color, m.name name,m.shopid shopid,ifnull(m.boxnum,0) boxnum,
		g.color catecolor,m.upc upc,g.name category,IFNULL(p.location,p.url) image,m.brand brand,
		m.price price, ifnull(i.fulfillable,0) fulfillable,ifnull(i.inbound,0) inbound,m.isDelete isDelete,
		c.name supplier, m.issfg issfg, m.remark remark, u.name operator,m.supplier supplierid,
		m.opttime opttime,m.MOQ,m.delivery_cycle,m.effectivedate
		createdate,m.purchaseUrl, d.length,d.width,d.height ,d.weight,
		ifnull(subi.needamount,0) needamount,
		ifnull(subi.needinboundamount,0) needinboundamount,
		(select   count(submaterialid)  from t_erp_material_consumable where submaterialid=m.id) mainsubnum,
		case when ifnull(sc.stockingCycle,0) then ifnull(sc.stockingCycle,0) 
		else CAST(ROUND((ifnull(subi.needamount,0)+ifnull(subi.needinboundamount,0))*ifnull(s.amount,0)/100, 0) AS SIGNED)  end stockingCycle,
		ci.amount planamount,case when ifnull(ci.amount,0)>0 then 1 else 0 end isplan,w.id warehouseid,w.name warehousename
	from t_erp_material m  
	left join  (select  c.submaterialid materialid,
				   sum( case when iv.status like 'inbound_%' then iv.quantity*c.amount else 0 end ) needinboundamount,
				   sum( case when iv.status like 'outbound_%' or  iv.status='fulfillable' then iv.quantity*c.amount else 0 end ) needamount
				from t_erp_material_consumable c 
				left join t_erp_inventory iv on iv.materialid=c.materialid and iv.shopid=#{param.shopid,jdbcType=CHAR} 
				                                  and iv.warehouseid = #{param.warehouseid,jdbcType=CHAR}
				left join t_erp_material m  on m.id=c.materialid
				where  m.shopid=#{param.shopid,jdbcType=CHAR} and m.isdelete=0
				group by c.submaterialid 
	) subi on subi.materialid=m.id
	left join t_dimensions d on d.id=m.pkgDimensions
	left join t_erp_material_category g on g.id=m.categoryid AND g.shopid=#{param.shopid,jdbcType=CHAR}
	left join t_picture p on m.image=p.id
	left join t_erp_customer c on c.id=m.supplier AND c.shopid=#{param.shopid,jdbcType=CHAR}
	left join t_erp_warehouse w on w.id= #{param.warehouseid,jdbcType=CHAR}
	left join t_erp_v3_purchase_plan_consumable_item ci on ci.warehouseid=w.id and ci.materialid=m.id and ci.shopid=m.shopid
	left join t_erp_material_consumable_safety_stock s on s.shopid=m.shopid
	left join t_erp_stockcycle sc on sc.warehouseid=w.id and sc.materialid=m.id
	left join ( select 
	            iv.materialid ,
				sum( case when iv.status like 'inbound_%' then iv.quantity else 0 end ) inbound,
				sum( case when iv.status like 'outbound_%' then iv.quantity else 0 end ) outbound,
				sum( case when iv.status='fulfillable' then iv.quantity else 0 end ) fulfillable
				from t_erp_inventory iv
				where iv.shopid=#{param.shopid,jdbcType=CHAR}
			    and iv.warehouseid = #{param.warehouseid,jdbcType=CHAR}
				group by iv.materialid
	) i on i.materialid=m.id
	left join t_userinfo u on u.id=m.operator
	where m.shopid=#{param.shopid,jdbcType=CHAR} and m.mtype=1 

		and m.isDelete = 0


		 and  vt.mid=m.id


		 and m.id = #{param.materialid,jdbcType=CHAR}


		and m.isDelete = 1


		and ci.amount>0


		and i.fulfillable > 0



			and (m.sku like #{param.search,jdbcType=CHAR})


			and (m.name like #{param.search,jdbcType=CHAR})


			and (m.remark like #{param.search,jdbcType=CHAR})


			and (c.name like #{param.search,jdbcType=CHAR})



		and

			m.issfg = #{status,jdbcType=INTEGER}



		and

			m.id = #{mid,jdbcType=CHAR}



		and (m.supplier = #{param.supplierid,jdbcType=CHAR}  or
			m.id  in(select materialid from t_erp_material_supplier WHERE supplierid=#{param.supplierid,jdbcType=CHAR})
		)


		and m.categoryid = #{param.categoryid,jdbcType=CHAR}


		and m.name like #{param.name,jdbcType=CHAR}


		and m.owner = #{param.owner,jdbcType=CHAR}


		and m.owner = #{param.myself,jdbcType=CHAR}


		and m.remark like #{param.remark,jdbcType=CHAR}


		and m.color like #{param.color,jdbcType=CHAR}


		and

			m.sku like #{sku,jdbcType=CHAR}


	) v

		 where   fulfillable<stockingCycle

select m.id, m.sku sku,m.color color, m.name name,m.shopid shopid,ifnull(m.boxnum,0) boxnum,
	   m.upc upc,g.name category,m.brand brand,
	   m.price price, ifnull(i.fulfillable,0) fulfillable,m.isDelete isDelete,
	  c.name supplier, m.issfg issfg, m.remark remark, u.name operator,m.supplier supplierid,
	  m.opttime opttime,m.MOQ,m.delivery_cycle,m.effectivedate
	  createdate,m.purchaseUrl, d.length,d.width,d.height ,d.weight,
      w.id warehouseid,w.name warehousename
from t_erp_material m  
left join t_dimensions d on d.id=m.pkgDimensions
left join t_erp_material_category g on g.id=m.categoryid AND g.shopid=#{param.shopid,jdbcType=CHAR}
left join t_erp_customer c on c.id=m.supplier AND c.shopid=#{param.shopid,jdbcType=CHAR}
left join t_erp_warehouse w on w.id= #{param.warehouseid,jdbcType=CHAR}
left join ( select 
            iv.materialid ,
			sum( case when iv.status like 'inbound_%' then iv.quantity else 0 end ) inbound,
			sum( case when iv.status like 'outbound_%' then iv.quantity else 0 end ) outbound,
			sum( case when iv.status='fulfillable' then iv.quantity else 0 end ) fulfillable
			from t_erp_inventory iv
			where iv.shopid=#{param.shopid,jdbcType=CHAR}
		    and iv.warehouseid = #{param.warehouseid,jdbcType=CHAR}
			group by iv.materialid
) i on i.materialid=m.id
left join t_userinfo u on u.id=m.operator
where m.shopid=#{param.shopid,jdbcType=CHAR} and m.mtype=2

	and m.isDelete = 0


	 and m.id = #{param.materialid,jdbcType=CHAR}


	and m.isDelete = 1


	and i.fulfillable > 0



		and (m.sku like #{param.search,jdbcType=CHAR})


		and (m.name like #{param.search,jdbcType=CHAR})


		and (m.remark like #{param.search,jdbcType=CHAR})


		and (c.name like #{param.search,jdbcType=CHAR})



	and

		m.id = #{mid,jdbcType=CHAR}



	and (m.supplier = #{param.supplierid,jdbcType=CHAR}  or
		m.id  in(select materialid from t_erp_material_supplier WHERE supplierid=#{param.supplierid,jdbcType=CHAR})
	)


	and m.categoryid = #{param.categoryid,jdbcType=CHAR}


	and m.name like #{param.name,jdbcType=CHAR}


	and m.owner = #{param.owner,jdbcType=CHAR}


	and m.owner = #{param.myself,jdbcType=CHAR}


	and m.remark like #{param.remark,jdbcType=CHAR}


	and

		m.sku like #{sku,jdbcType=CHAR}

select m.id, m.sku sku,m.color color, m.name name,m.shopid shopid,ifnull(m.boxnum,0) boxnum,
	g.color catecolor,m.upc upc,g.name category,IFNULL(p.location,p.url) image,m.brand brand,
	m.price price, ifnull(i.fulfillable,0) fulfillable,m.isDelete isDelete,
	c.name supplier, m.issfg issfg, m.remark remark, u.name operator,m.supplier supplierid,
	m.opttime opttime,m.MOQ,m.delivery_cycle,m.effectivedate
	createdate,m.purchaseUrl, d.length,d.width,d.height ,d.weight,

	vt.tagids,


	 (
	     SELECT GROUP_CONCAT(t.tagid) tagids
		  FROM t_erp_material_tags t
		  where t.mid=m.id
	 ) tagids,

	m.specification
from t_erp_material m
left join t_dimensions d on d.id=m.pkgDimensions
left join t_erp_material_category g on g.id=m.categoryid AND g.shopid=#{param.shopid,jdbcType=CHAR}
left join t_picture p on m.image=p.id

left join (SELECT t.mid,GROUP_CONCAT(t.tagid) tagids FROM   t_erp_material_tags t  
			WHERE 
				t.tagid IN (

						#{tagid,jdbcType=CHAR} 

				)
		   group by t.mid
      ) vt on vt.mid=m.id

left join t_erp_customer c on c.id=m.supplier AND c.shopid=#{param.shopid,jdbcType=CHAR}
left join ( select MAX(iv.shopid) shopid,MAX(iv.warehouseid) warehouseid,iv.materialid ,
			sum( case when iv.status like 'inbound_%' then iv.quantity else 0 end ) inbound,
			sum( case when iv.status like 'outbound_%' then iv.quantity else 0 end ) outbound,
			sum( case when iv.status='fulfillable' then iv.quantity else 0 end ) fulfillable
			from t_erp_inventory iv

			left join t_erp_warehouse w on w.id=iv.warehouseid

			where iv.shopid=#{param.shopid,jdbcType=CHAR}

						and iv.warehouseid = #{param.warehouseid,jdbcType=CHAR}


						and iv.materialid = #{param.materialid,jdbcType=CHAR}


						and w.addressid = #{param.addressid,jdbcType=CHAR}

			group by iv.materialid
) i on i.materialid=m.id
left join t_userinfo u on u.id=m.operator
where m.shopid=#{param.shopid,jdbcType=CHAR}

   and  m.mtype = #{param.mtype,jdbcType=INTEGER}


	and m.isDelete = 0


	 and  vt.mid=m.id


	 and m.id = #{param.materialid,jdbcType=CHAR}


	and m.isDelete = 1


	and i.fulfillable > 0



		and (m.sku like #{param.search,jdbcType=CHAR})


		and (m.name like #{param.search,jdbcType=CHAR})


		and (m.remark like #{param.search,jdbcType=CHAR})


		and (c.name like #{param.search,jdbcType=CHAR})



	and

		m.issfg = #{status,jdbcType=INTEGER}



	and

		m.id = #{mid,jdbcType=CHAR}



	and (m.supplier = #{param.supplierid,jdbcType=CHAR}  or
		m.id  in(select materialid from t_erp_material_supplier WHERE supplierid=#{param.supplierid,jdbcType=CHAR})
	)


	and m.categoryid = #{param.categoryid,jdbcType=CHAR}


	and m.name like #{param.name,jdbcType=CHAR}


	and m.owner = #{param.owner,jdbcType=CHAR}


	and m.owner = #{param.myself,jdbcType=CHAR}


	and m.remark like #{param.remark,jdbcType=CHAR}


	and m.color like #{param.color,jdbcType=CHAR}


	and

		m.sku like #{sku,jdbcType=CHAR}

select m.id, m.sku sku,m.color color, m.name name,m.shopid shopid,ifnull(m.boxnum,0) boxnum,
	g.color catecolor,m.upc upc,g.name category,IFNULL(p.location,p.url) image,m.brand brand,
	q.price price, ifnull(i.fulfillable,0) fulfillable,m.isDelete isDelete,
	c.name supplier, m.issfg issfg, m.remark remark, u.name operator,m.supplier supplierid,
	m.opttime opttime,m.MOQ,m.delivery_cycle,m.effectivedate
	createdate,m.purchaseUrl, d.length,d.width,d.height ,d.weight
from t_erp_material m
left join t_dimensions d on d.id=m.pkgDimensions
left join t_erp_material_category g on g.id=m.categoryid AND g.shopid=#{shopid,jdbcType=CHAR}
left join t_picture p on m.image=p.id
left join t_erp_customer c on c.id=m.supplier AND c.shopid=#{shopid,jdbcType=CHAR}
left join ( select shopid,max(warehouseid) warehouseid,materialid ,
			sum( case when status='inbound' then quantity else 0 end ) inbound,
			sum( case when status='outbound' then quantity else 0 end ) outbound,
			sum( case when status='fulfillable' then quantity else 0 end ) fulfillable
			from t_erp_inventory 
			 where shopid=#{shopid,jdbcType=CHAR}
			 group by shopid, materialid
) i on i.materialid=m.id
left join ( SELECT material,max(price) price 
			FROM t_erp_stepwise_quotn group by material
) q on m.id= q.material
left join t_userinfo u on u.id=m.operator
where m.shopid=#{shopid,jdbcType=CHAR}
	and m.isDelete = 0

	and i.warehouseid = #{warehouseid,jdbcType=CHAR}


	and m.supplier = #{supplierid,jdbcType=CHAR}


	and m.owner = #{myself,jdbcType=CHAR}


	and

		m.sku = #{sku,jdbcType=CHAR}

select id,sku as name from t_erp_material where shopid=#{shopid,jdbcType=CHAR} and isDelete = 0
and sku like #{sku,jdbcType=CHAR}
order by sku limit 0,10

select id,sku as label from t_erp_material where shopid=#{shopid,jdbcType=CHAR} and isDelete = 0
and sku like #{sku,jdbcType=CHAR}
order by sku

SELECT m.price,d.length,d.width,d.height,d.weight 
from t_erp_material m
left join t_dimensions d on m.pkgDimensions=d.id
where shopid=#{shopid,jdbcType=CHAR} 
	and sku=#{sku,jdbcType=CHAR} 
	and isDelete = 0 limit 0,1;

select tp.sku,tp.asin,dm.*
from t_product_info tp
left join t_product_in_opt opt on opt.pid = tp.id
left join t_amazon_auth a on a.id=tp.amazonAuthId
left join t_erp_material m on m.sku = ifnull(opt.msku,tp.sku) and m.shopid = a.shop_id
left join t_dimensions dm on dm.id=m.pkgDimensions
where tp.sku = #{sku,jdbcType=CHAR}
	and m.isDelete = 0
	and a.shop_id = #{shopid,jdbcType=CHAR}
	and a.groupid = #{groupid,jdbcType=CHAR}
	and tp.marketplaceid = #{marketplaceid,jdbcType=CHAR}

select m.id,m.sku,m.id materialid
from t_erp_material m
where m.shopid=#{shopid,jdbcType=CHAR} 
	and ifnull(m.isDelete,0) = 0 
	and m.sku in (select IFNULL(o.msku,p.sku) 
				  from t_product_info p
				  LEFT JOIN t_product_in_opt o ON o.pid=p.id
				  left join t_amazon_auth a on p.amazonAuthId =a.id
				  where a.shop_id=#{shopid,jdbcType=CHAR} 

						 and a.groupid=#{groupid,jdbcType=CHAR} 

				  and ifnull(o.disable,false)=false
				  AND ifnull(p.invalid,FALSE)=false
	)

select t.sku
from t_erp_material t 
where  t.shopid=#{shopid,jdbcType=CHAR}  and t.isDelete = 0 

	and t.color = #{color,jdbcType=CHAR}


	and t.owner = #{owner,jdbcType=CHAR}

SELECT t.id,t.sku,t.name,case when t.isSmlAndLight=1 then '是' ELSE '否' end isSmallLight,t.price price,t.boxnum, CONCAT(ifnull(d.length,0),'-',ifnull(d.width,0),'-',ifnull(d.height,0),'-',ifnull(d.weight,0)) pageDimensions, CONCAT(IFNULL(d2.length,0),'-',IFNULL(d2.width,0),'-',IFNULL(d2.height,0),'-',IFNULL(d2.weight,0)) itemDimensions, CONCAT(IFNULL(d3.length,0),'-',IFNULL(d3.width,0),'-',IFNULL(d3.height,0),'-',IFNULL(d3.weight,0)) boxDimensions, case when t.issfg=2 then '半成品' when t.issfg=1 then '组装成品' ELSE '单独成品' end issfg, v2.ass asslist, v3.consumablelist, t.badrate,t.vatrate, t.brand,t.specification,cate.name category,ui.name,t.effectivedate,cust.name supplier, t.delivery_cycle deliverycycle,t.other_cost othercost,t.productCode purchasecode,t.purchaseUrl purchaseurl, t.color color,t.remark,sum(v.inbound) inbound,sum(v.qty) qty,ui.name owner, case when t.isDelete=1 then '是' else '否' end isDelete FROM t_erp_material t LEFT JOIN t_dimensions d ON t.pkgDimensions=d.id LEFT JOIN t_dimensions d2 ON t.itemDimensions=d2.id LEFT JOIN t_dimensions d3 ON t.boxDimensions=d3.id LEFT JOIN t_erp_material_category cate ON cate.id=t.categoryid LEFT JOIN t_userinfo ui ON ui.id=t.owner LEFT JOIN t_erp_customer cust ON cust.id=t.supplier LEFT JOIN ( SELECT mainmid, GROUP_CONCAT( CONCAT(tm.sku,'-',subnumber,'-',tm.assembly_time)) AS ass FROM t_erp_assembly a LEFT JOIN t_erp_material tm on tm.id=a.submid WHERE shopid=#{shopid,jdbcType=CHAR} GROUP BY mainmid ) v2 ON v2.mainmid=t.id LEFT JOIN ( SELECT warehouseid,materialid , SUM(case when 'fulfillable'=STATUS OR STATUS LIKE 'outbound_%' then quantity ELSE 0 END ) qty, SUM(case when STATUS LIKE 'inbound_purchase' then quantity ELSE 0 END ) inbound FROM t_erp_inventory WHERE shopid=#{shopid,jdbcType=CHAR} GROUP BY shopid,warehouseid,materialid ) v ON v.materialid=t.id LEFT JOIN ( SELECT materialid, GROUP_CONCAT( CONCAT(tm.sku,'-',a.amount)) AS consumablelist FROM t_erp_material_consumable a LEFT JOIN t_erp_material tm on tm.id=a.submaterialid WHERE shopid=#{shopid,jdbcType=CHAR} GROUP BY a.materialid ) v3 ON v3.materialid=t.id WHERE t.shopid=#{shopid,jdbcType=CHAR}
and t.isDelete = 0
and t.isDelete = 1
and (t.sku like #{search,jdbcType=CHAR} or t.name like #{search,jdbcType=CHAR} or t.remark like #{search,jdbcType=CHAR})
and
t.issfg = #{status,jdbcType=INTEGER}
and v.warehouseid = #{warehouseid,jdbcType=CHAR}
and t.supplier = #{supplierid,jdbcType=CHAR}
and t.categoryid = #{categoryid,jdbcType=CHAR}
and t.name like #{name,jdbcType=CHAR}
and t.owner = #{owner,jdbcType=CHAR}
and t.owner = #{myself,jdbcType=CHAR}
and t.remark like #{remark,jdbcType=CHAR}
and t.color like #{color,jdbcType=CHAR}
and
t.sku like #{sku,jdbcType=CHAR}
group by t.id

select c.name supplier,g.name category,u.name ownername, m.supplier supplierId , IFNULL(p.location,p.url) image, m.id, m.sku,m.name,m.shopid,m.upc,bran.name brand,bran.id brandid,m.itemDimensions,m.pkgDimensions,m.boxDimensions, m.boxnum, m.specification,m.badrate,m.vatrate,m.productCode,m.delivery_cycle,m.other_cost,m.MOQ, m.purchaseUrl, m.remark,m.categoryid,m.issfg,m.color,m.owner,m.operator,m.opttime,m.price, m.createdate, m.creator,m.parentid,m.isSmlAndLight,m.assembly_time,m.isDelete,m.effectivedate,m.image imageid, i.inbound,i.outbound,i.fulfillable from t_erp_material m left join t_erp_material_category g on g.id=m.categoryid left join t_picture p on m.image=p.id left join t_erp_customer c on c.id=m.supplier left join t_userinfo u on u.id=m.owner left join t_erp_material_brand bran on bran.id=m.brand left join ( select shopid,warehouseid,materialid , sum( case when status like 'inbound_%' then quantity else 0 end ) inbound, sum( case when status like 'outbound_%' then quantity else 0 end ) outbound, sum( case when status='fulfillable' then quantity else 0 end ) fulfillable from t_erp_inventory where shopid=#{shopid,jdbcType=CHAR} group by shopid, materialid ) i on i.materialid=m.id where m.shopid=#{shopid,jdbcType=CHAR} and m.isdelete=false
and m.owner=#{owner,jdbcType=CHAR}
and m.sku in
#{msku,jdbcType=CHAR}
