mapper.select:
  	SELECT e.*,f.*,c.name supplierName,c.number supplierNum from t_erp_purchase_form_entry e 
	 left join t_erp_purchase_form f on e.formid = f.id 
	 left join t_erp_customer c on e.supplier = c.id 
	 where e.materialid = #{materialid,jdbcType=CHAR} 
	 order by f.createdate desc limit 3;

        select ifnull(count(1),0) from t_erp_purchase_form_entry e
		left join  t_erp_purchase_form f on e.formid=f.id 
		where e.auditstatus=1 and   f.shopid=#{shopid,jdbcType=CHAR}

select
m.sku
c.name supplier
,sum(v3.entryin) totalin,sum(entry.totalin) actual_totalin ,sum( v2.entrycostfee) totalcostfee, sum( case when auditstatus=3 then v2.entrycostfee else 0 end)/ sum(case when auditstatus=3 then entry.totalin else 0 end) pprice, sum(entry.amount) orderamount, sum(entry.amount*ifnull(ds.weight,0)) weight, avg(entry.itemprice) itemprice, sum(entry.orderprice) totalorderprice,sum(v3.entryre) totalre, sum(v2.entryshipfee) totalshipfee , sum(v2.entryotherfee) totalotherfee , sum(case when auditstatus=3 then v2.entrycostfee-entry.orderprice else 0 end ) lesspay, sum(case when auditstatus=3 then v3.entryin-v3.entryre-entry.amount else 0 end) lessrec, sum(case when auditstatus=2 then entry.amount-ifnull(entry.totalin,0) else 0 end ) needin, sum(case when auditstatus=2 and (entry.orderprice-ifnull(v2.entrycostfee,0))>0 then entry.orderprice-ifnull(v2.entrycostfee,0)-ifnull(entryappcostfee,0) else 0 end ) waitPay , sum(case when auditstatus=2 and (entry.orderprice-ifnull(v2.entrycostfee,0))>0 then ifnull(entryappcostfee,0) else 0 end ) appwaitPay from t_erp_purchase_form_entry entry left join t_erp_purchase_form form on form.id=entry.formid left join t_erp_customer c on c.id=entry.supplier left join t_erp_warehouse w on form.warehouseid=w.id
left join t_erp_material m on m.id=entry.materialid left join t_dimensions ds on ds.id=m.pkgDimensions left join ( select pay.formentryid, sum(case when auditstatus=1 and projectid='26138972997300238' then payprice else 0 end ) entryshipfee, sum(case when auditstatus=1 and projectid='26138972997300240' then payprice else 0 end ) entrycostfee, sum(case when auditstatus=2 and projectid='26138972997300240' then payprice else 0 end ) entryappcostfee, sum(case when auditstatus=1 and projectid!='26138972997300240' and projectid!='26138972997300238' then payprice else 0 end ) entryotherfee from t_erp_purchase_form_payment pay group by pay.formentryid ) v2 on v2.formentryid=entry.id left join ( select rec.formentryid,sum(case when ftype='in' then amount else 0 end ) entryin, sum(case when ftype='out' then amount else 0 end ) entryre from t_erp_purchase_form_receive rec group by rec.formentryid) v3 on v3.formentryid=entry.id where entry.auditstatus>1 and form.shopid=#{param.shopid,jdbcType=CHAR} and form.createdate >=#{param.fromDate,jdbcType=DATE} and form.createdate <=#{param.endDate,jdbcType=DATE}
and m.sku is not null
and c.name is not null
and w.id=#{param.warehouseid,jdbcType=CHAR}
and
m.sku like #{param.search,jdbcType=CHAR}
c.name like #{param.search,jdbcType=CHAR}
group by entry.materialid
group by entry.supplier

select sum(S.actual_totalin) actual_totalin, sum(S.totalin) totalin,sum(S.totalcostfee) totalcostfee, sum(S.orderamount) orderamount, sum(S.weight) weight, sum(S.totalorderprice) totalorderprice, sum(S.totalre) totalre,sum(S.totalshipfee) totalshipfee, sum(S.totalotherfee) totalotherfee, sum(S.lesspay) lesspay,sum(S.lessrec) lessrec, sum(S.needin) needin,sum(S.waitPay) waitPay from( select sum(entry.totalin) actual_totalin, sum(v3.entryin) totalin ,sum( v2.entrycostfee) totalcostfee, sum(entry.amount) orderamount, sum(entry.orderprice) totalorderprice, sum(entry.amount*ifnull(ds.weight,0)) weight, sum(v3.entryre) totalre, sum(v2.entryshipfee) totalshipfee , sum(v2.entryotherfee) totalotherfee , sum(case when auditstatus=3 then v2.entrycostfee-entry.orderprice else 0 end ) lesspay, sum(case when auditstatus=3 then v3.entryin-v3.entryre-entry.amount else 0 end) lessrec, sum(case when (auditstatus=2 or auditstatus=1) then entry.amount-ifnull(entry.totalin,0) else 0 end ) needin, sum(case when (auditstatus=2 or auditstatus=1)and (entry.orderprice-ifnull(v2.entrycostfee,0))>0 then entry.orderprice-ifnull(v2.entrycostfee,0) else 0 end ) waitPay from t_erp_purchase_form_entry entry left join t_erp_purchase_form form on form.id=entry.formid left join t_erp_customer c on c.id=entry.supplier left join t_erp_warehouse w on form.warehouseid=w.id left join t_erp_material m on m.id=entry.materialid left join t_dimensions ds on ds.id=m.pkgDimensions left join ( select pay.formentryid, sum(case when auditstatus=1 and projectid='26138972997300238' then payprice else 0 end ) entryshipfee, sum(case when auditstatus=1 and projectid='26138972997300240' then payprice else 0 end ) entrycostfee, sum(case when auditstatus=2 and projectid='26138972997300240' then payprice else 0 end ) entryappcostfee, sum(case when auditstatus=1 and projectid!='26138972997300240' and projectid!='26138972997300238' then payprice else 0 end ) entryotherfee from t_erp_purchase_form_payment pay group by pay.formentryid ) v2 on v2.formentryid=entry.id left join ( select rec.formentryid,sum(case when ftype='in' then amount else 0 end ) entryin, sum(case when ftype='out' then amount else 0 end ) entryre from t_erp_purchase_form_receive rec group by rec.formentryid) v3 on v3.formentryid=entry.id where form.shopid=#{shopid,jdbcType=CHAR} and entry.auditstatus>1 and form.createdate >=#{fromDate,jdbcType=DATE} and form.createdate <=#{endDate,jdbcType=DATE}
and m.sku is not null
and c.name is not null
and w.parentid=#{warehouseid,jdbcType=CHAR}
and
m.sku like #{search,jdbcType=CHAR}
c.name like #{search,jdbcType=CHAR}
group by entry.materialid
group by entry.supplier
) S
