mapper.select:
select pay.opttime,pay.payprice,form.number,entry.paystatus,entry.totalin, form.createdate,m.sku, p.location image,m.name mname,entry.amount purchases,w.name wname,c.name cname, tp.name fee_type , pay.remark, entry.orderprice, pmd.name payment_method,entry.totalpay, (entry.orderprice-entry.totalpay) need_pay, u.name from t_erp_purchase_form_payment pay left join t_erp_purchase_form_payment_method pmd on pmd.id=pay.payment_method left join t_erp_fin_project tp on tp.id=pay.projectid left join t_erp_purchase_form_entry entry on entry.id=pay.formentryid left join t_userinfo u on u.id=pay.operator left join t_erp_material m on m.id=entry.materialid left join t_erp_purchase_form form on form.id=entry.formid left join t_erp_customer c on c.id=entry.supplier left join t_picture p on p.id=m.image left join t_erp_warehouse w on w.id=form.warehouseid where entry.auditstatus>1 and form.shopid=#{param.shopid,jdbcType=CHAR} and pay.auditstatus=1 and pay.opttime >=#{param.fromDate,jdbcType=DATE} and pay.opttime <=#{param.endDate,jdbcType=DATE}
and m.sku like #{param.search,jdbcType=CHAR}
and (form.number like #{param.search,jdbcType=CHAR} )
and pay.projectid= #{param.projectid,jdbcType=CHAR}
and form.warehouseid= #{param.warehouseid,jdbcType=CHAR}
and entry.supplier= #{param.supplierid,jdbcType=CHAR}
and pay.payment_method= #{param.paymethod,jdbcType=CHAR}

          select sum(pay.payprice) totalpayprice,sum(entry.amount*entry.itemprice-entry.totalpay) totalneedpay ,sum(entry.orderprice) totalorderprice
		from t_erp_purchase_form_payment pay
		left join t_erp_purchase_form_entry entry on entry.id=pay.formentryid
		left join t_userinfo u on u.id=pay.operator
		left join t_erp_material m on m.id=entry.materialid
		left join t_erp_purchase_form form on form.id=entry.formid 
	where entry.auditstatus>1 and form.shopid=#{shopid,jdbcType=CHAR} and pay.auditstatus=1
	 and pay.opttime >=#{fromDate,jdbcType=DATE}
    and  pay.opttime <=#{endDate,jdbcType=DATE} 



    			and	m.sku like #{search,jdbcType=CHAR}


    			 and  form.number like #{search,jdbcType=CHAR} 




    			 and   form.warehouseid= #{warehouseid,jdbcType=CHAR} 


    			 and   pay.projectid= #{projectid,jdbcType=CHAR} 


    			 and   entry.supplier= #{supplierid,jdbcType=CHAR} 


    			 and   pay.payment_method= #{paymethod,jdbcType=CHAR}
