mapper.select:
select rec.opttime,rec.amount,form.number,form.createdate,m.sku, p.location image,m.name mname,entry.amount purchases,w.name wname,c.name cname, entry.orderprice purchaseprice, case when rec.ftype='in' then '入库' ELSE case when rec.ftype='clear' then '撤销入库操作' else '退货' END END ftype,rec.remark, entry.amount-entry.totalin needin,u.name,entry.audittime,entry.deliverydate from t_erp_purchase_form_receive rec left join t_erp_purchase_form_entry entry on entry.id=rec.formentryid left join t_erp_customer c on c.id=entry.supplier left join t_userinfo u on u.id=rec.operator left join t_erp_material m on m.id=entry.materialid left join t_picture p on p.id=m.image left join t_erp_purchase_form form on form.id=entry.formid left join t_erp_warehouse w on w.id=form.warehouseid where entry.auditstatus>1 and form.shopid=#{param.shopid,jdbcType=CHAR} and rec.opttime >=#{param.fromDate,jdbcType=DATE} and rec.opttime <=#{param.endDate,jdbcType=DATE}
and rec.warehouseid=#{param.warehouseid,jdbcType=CHAR}
and m.sku like #{param.search,jdbcType=CHAR}
and (form.number like #{param.search,jdbcType=CHAR})

select sum(case when rec.ftype='in' then rec.amount else case when rec.ftype='re' then rec.amount*-1 else 0 end end ) totalamount from t_erp_purchase_form_receive rec left join t_erp_purchase_form_entry entry on entry.id=rec.formentryid left join t_userinfo u on u.id=rec.operator left join t_erp_material m on m.id=entry.materialid left join t_picture p on p.id=m.image left join t_erp_purchase_form form on form.id=entry.formid where entry.auditstatus>1 and form.shopid=#{param.shopid,jdbcType=CHAR} and rec.opttime >=#{param.fromDate,jdbcType=DATE} and rec.opttime <=#{param.endDate,jdbcType=DATE}
and rec.warehouseid=#{param.warehouseid,jdbcType=CHAR}
and m.sku like #{param.search,jdbcType=CHAR}
and (form.number like #{param.search,jdbcType=CHAR})
