mapper.select:
select s.id,concat(case when s.subarea is null or s.subarea='' then '' else concat(s.subarea,'-') end, case when ts.name is null then '' else concat(ts.name,'-') end , case when c.name is null then '' else concat(c.name,'-') end ,s.channame) channame , s.price, s.drate, s.pretime, s.priceunits from t_erp_ship_transcompany t left join t_erp_ship_transdetail s on s.company=t.id left join t_erp_ship_transchannel c on c.id=s.channel left join t_erp_transtype ts on ts.id=s.transtype where t.id=#{company,jdbcType=CHAR} and t.shopid=#{shopid,jdbcType=CHAR} and s.disabled=0
and s.marketplaceid=#{marketplaceid,jdbcType=CHAR}
and s.transtype=#{transtype,jdbcType=CHAR}

   		 select s.id,s.company,s.channame,s.price,sum(s.pretime) needday,s.priceunits 
    from t_erp_ship_transcompany t 
	left join t_erp_ship_transdetail s on s.company=t.id
	where t.id=#{company,jdbcType=CHAR} and s.id=#{channel,jdbcType=CHAR} and s.disabled=0

	and s.marketplaceid=#{marketplaceid,jdbcType=CHAR}


	and s.transtype=#{transtype,jdbcType=CHAR}

select t.*,co.name cname,ty.name tname,tk.name market,info.name optname ,tst.name channeltype from t_erp_ship_transdetail t left join t_erp_ship_transcompany co on co.id=t.company left join t_erp_transtype ty on ty.id=t.transtype left join t_marketplace tk on tk.marketplaceid=t.marketplaceid left join t_erp_ship_transchannel tst on tst.id=t.channel left join t_userinfo info on info.id=t.operator where co.shopid=#{shopid,jdbcType=CHAR} and t.disabled=0 and t.price>0
and t.marketplaceid =#{marketplaceid,jdbcType=CHAR}
and t.transtype =#{transtype,jdbcType=CHAR}
and t.priceunits =#{priceunits,jdbcType=CHAR}
and t.company =#{company,jdbcType=CHAR}
and (t.channame like #{search,jdbcType=CHAR} or t.remark like #{search,jdbcType=CHAR})

   	SELECT  t.* FROM t_erp_ship_transcompany t
	LEFT JOIN t_erp_ship_transdetail s ON t.id=s.company 
	WHERE t.isdelete=0 AND s.transtype=#{transtype,jdbcType=CHAR}
	and s.marketplaceid =#{marketplaceid,jdbcType=CHAR}
	AND t.shopid=#{shopid,jdbcType=CHAR} and s.disabled=0
	GROUP BY t.id
