mapper.select:
select a.id, max(a.number) number, max(u.name) creator, max(a.auditstatus) auditstatus, max(a.remark) remark, max(a.opttime) opttime, max(wfrom.name) wfname, max(a.groupid) groupid, max(wto.country) country, max(wto.name) wtname , count(b.id) skunum, max(c.name) company, max(tc.name) transtypename, max(a.createdate) createdate from t_erp_dispatch_oversea_form a LEFT JOIN t_userinfo u on a.operator = u.id left join t_erp_warehouse wfrom on a.from_warehouseid=wfrom.id left join t_erp_warehouse wto on a.to_warehouseid =wto.id LEFT JOIN t_erp_dispatch_oversea_form_entry b on b.formid=a.id left join t_erp_dispatch_oversea_trans t on t.formid=a.id left join t_erp_ship_transcompany c on c.id=t.company left join t_erp_ship_transchannel tc on tc.id=t.channel LEFT join t_erp_material m on b.materialid = m.id where a.shopid = #{params.shopid,jdbcType=CHAR}
and (m.sku like #{params.search,jdbcType=CHAR} or m.name like #{params.search,jdbcType=CHAR} or a.number like #{params.search,jdbcType=CHAR})
and a.from_warehouseid= #{params.fromwid,jdbcType=CHAR}
and a.to_warehouseid= #{params.towid,jdbcType=CHAR}
and
a.auditstatus = #{status,jdbcType=INTEGER}
and DATE_FORMAT(a.opttime,'%Y-%m-%d') >=#{params.fromDate,jdbcType=DATE} and DATE_FORMAT(a.opttime,'%Y-%m-%d') <=#{params.endDate,jdbcType=DATE}
group by a.id
